from collections import deque

def longestPathAndMinHeight(n, edges):
    if n == 0:
        return (0, 0)

    # Build adjacency list
    adj = [[] for _ in range(n + 1)]
    for u, v in edges:
        adj[u].append(v)
        adj[v].append(u)

    # Function to compute the diameter using BFS twice
    def bfs(start):
        visited = [-1] * (n + 1)
        q = deque([start])
        visited[start] = 0
        max_dist = 0
        far_node = start
        while q:
            u = q.popleft()
            for v in adj[u]:
                if visited[v] == -1:
                    visited[v] = visited[u] + 1
                    q.append(v)
                    if visited[v] > max_dist:
                        max_dist = visited[v]
                        far_node = v
        return far_node, max_dist

    # First BFS to find one end of the diameter
    u, _ = bfs(1)
    # Second BFS to find the other end and calculate the diameter
    v, diameter = bfs(u)

    # Function to compute minimal height when rooted at u
    def compute_min_height(u, parent):
        children = [v for v in adj[u] if v != parent]
        if not children:
            return 0
        h_list = [compute_min_height(v, u) for v in children]
        h_list.sort()
        min_max = float('inf')
        for k in range(0, len(h_list) + 1):
            left_max = h_list[k-1] if k > 0 else 0
            right_max = h_list[-1] if k < len(h_list) else 0
            current_max = max(left_max, right_max)
            if current_max < min_max:
                min_max = current_max
        return min_max + 1

    # Find the minimum height across all possible roots
    min_height = float('inf')
    for root in range(1, n + 1):
        current_height = compute_min_height(root, None)
        if current_height < min_height:
            min_height = current_height

    return (diameter, min_height)

def search_matrix(matrix, target):
    if not matrix or not matrix[0]:
        return False
    m, n = len(matrix), len(matrix[0])
    i, j = 0, n - 1
    while i < m and j >= 0:
        current = matrix[i][j]
        if current == target:
            return True
        elif current < target:
            i += 1
        else:
            j -= 1
    return False

