def solve_linear_equation(a1, b1, c1, a2, b2, c2):
    # Check for invalid equations (0x + 0y = non-zero)
    if (a1 == 0 and b1 == 0 and c1 != 0) or (a2 == 0 and b2 == 0 and c2 != 0):
        raise ValueError("Invalid equation: 0x + 0y = non-zero constant")

    # Calculate the determinant
    D = a1 * b2 - a2 * b1

    if D == 0:
        # Check if the system is dependent or inconsistent
        if (a1 * c2 == a2 * c1) and (b1 * c2 == b2 * c1):
            raise ValueError("The system has infinitely many solutions.")
        else:
            raise ValueError("The system has no solution.")
    else:
        # Calculate the numerators for x and y
        Dx = c1 * b2 - c2 * b1
        Dy = a1 * c2 - a2 * c1

        x = Dx / D
        y = Dy / D

        return (x, y)

def length_of_longest_substring(s: str) -> int:
    last_seen = {}
    max_len = 0
    start = 0
    for end, char in enumerate(s):
        if char in last_seen and last_seen[char] >= start:
            start = last_seen[char] + 1
        last_seen[char] = end
        current_len = end - start + 1
        if current_len > max_len:
            max_len = current_len
    return max_len

def max_root_to_leaf_path_sum(arr):
    def helper(i):
        if i >= len(arr) or arr[i] == -1:
            return float('-inf')
        left = helper(2 * i + 1)
        right = helper(2 * i + 2)
        max_child = max(left, right)
        if max_child == float('-inf'):
            return arr[i]
        else:
            return arr[i] + max_child
    result = helper(0)
    return result if result != float('-inf') else 0

def minPathSum(matrix):
    if not matrix or not matrix[0]:
        return 0
    m = len(matrix)
    n = len(matrix[0])
    dp = [0] * n
    dp[0] = matrix[0][0]
    for j in range(1, n):
        dp[j] = dp[j-1] + matrix[0][j]
    for i in range(1, m):
        new_dp = [0] * n
        new_dp[0] = dp[0] + matrix[i][0]
        for j in range(1, n):
            new_dp[j] = matrix[i][j] + min(new_dp[j-1], dp[j])
        dp = new_dp
    return dp[-1]

def minimize_max_task_duration(tasks):
    total = sum(tasks)
    half = total // 2
    dp = [False] * (half + 1)
    dp[0] = True
    max_subset = 0
    for task in tasks:
        for j in range(half, task - 1, -1):
            if dp[j - task]:
                if not dp[j]:
                    dp[j] = True
                    if j > max_subset:
                        max_subset = j
    return total - max_subset

